import React, { useState, useRef } from "react";
import axios from "axios";
import { ClipLoader } from "react-spinners";

import Select from "react-select";
import { LANGUAGES } from "../constants.js";
import "./audioRecorder.css";

const AudioRecorder = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [showLongProcessMessage, setShowLongProcessMessage] = useState(false);
  const [selectedLanguageCode, setSelectedLanguageCode] = useState("");
  const [pitches, setPitches] = useState([]);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const languageOptions = Object.entries(LANGUAGES).map(([name, code]) => ({
    value: code,
    label: name.charAt(0).toUpperCase() + name.slice(1),
  }));

  const handleChange = (selectedOption) => {
    setSelectedLanguageCode(selectedOption.value);
  };

  const uploadAudio = async (audioBlob) => {
    setIsUploading(true);
    setShowLongProcessMessage(false);

    const timer = setTimeout(() => {
      setShowLongProcessMessage(true);
    }, 10000);

    const audioFile = new File([audioBlob], "recording.mp3", {
      type: "audio/mp3",
    });

    const formData = new FormData();
    formData.append("file", audioFile);
    formData.append("language", selectedLanguageCode);

    try {
      const response = await axios.post(
        "/api/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      console.log("File successfully uploaded", response.data);

      if (response.data.lyrics) {
        const pitchData = new FormData();
        pitchData.append("file", audioFile);
        pitchData.append("lyrics", JSON.stringify(response.data.lyrics));
        console.log(pitchData);
        await pitchAnalysis(pitchData);
      }
    } catch (error) {
      console.error("Error uploading file", error.response.data);
    } finally {
      setIsUploading(false);
      setShowLongProcessMessage(false);
      clearTimeout(timer);
    }
  };

  const pitchAnalysis = async (formData) => {
    try {
      const response = await axios.post(
        "/api/analyze-pitches",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      console.log("File successfully uploaded", response.data);
      setPitches(response.data.pitches);
    } catch (error) {
      console.error("Error uploading file", error.response.data);
    } finally {
      setIsUploading(false);
    }
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, {
          type: "audio/mp3",
        });
        await uploadAudio(audioBlob);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (err) {
      console.error("Error starting audio recording:", err);
    }
  };

  const handleStop = () => {
    setIsRecording(false);
    mediaRecorderRef.current.stop();
  };

  return (
    <div className="container">
      {isUploading ? (
        <div className="spinner-container">
          <ClipLoader color="#FFF" size={50} />
          {showLongProcessMessage && <p>This may take a while...</p>}
        </div>
      ) : (
        <div className="container">
          <p>Record Audio Here:</p>
          <div>
            <button
              onClick={isRecording ? handleStop : startRecording}
              className={`record-button ${isRecording ? "recording" : ""}`}
            >
              <div className={isRecording ? "inner-square" : "inner-circle"} />
            </button>
            <Select
              options={languageOptions}
              onChange={handleChange}
              placeholder="Select a language..."
              styles={{
                control: (provided) => ({
                  ...provided,
                  backgroundColor: "darkgray",
                  color: "white",
                }),
                singleValue: (provided) => ({
                  ...provided,
                  color: "white",
                }),
                option: (provided, state) => ({
                  ...provided,
                  backgroundColor: state.isSelected ? "gray" : "darkgray",
                  color: "white",
                  ":hover": {
                    backgroundColor: "gray",
                  },
                }),
                placeholder: (provided) => ({
                  ...provided,
                  color: "white",
                }),
                input: (provided) => ({
                  ...provided,
                  color: "white",
                }),
              }}
            />
          </div>
          {pitches
            ? pitches.map((lyric) => (
                <div key={lyric.word}>
                  <p>{lyric.pitch}</p>
                  <h1>{lyric.word}</h1>
                </div>
              ))
            : null}
        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
