import React, { useState, useRef } from "react";
import axios from "axios";
import { ClipLoader } from "react-spinners";
import Select from "react-select";
import { scaleOptions, languageOptions, selectStyle} from "../constants";
import "./audioRecorder.css";

const AudioRecorder = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [showLongProcessMessage, setShowLongProcessMessage] = useState(false);
  const [selectedLanguageCode, setSelectedLanguageCode] = useState("");
  const [selectedScale, setSelectedScale] = useState("");
  const [pitches, setPitches] = useState([]);
  const [processedAudio, setProcessedAudio] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const handleChangeLanguage = (selectedOption) => {
    setSelectedLanguageCode(selectedOption.value);
  };

  const handleChangeScale = (selectedOption) => {
    setSelectedScale(selectedOption.value);
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    console.log(file);
    if (file) {
      setUploadedFile(file);
    }
  };

  const submitAudio = async () => {
    if (uploadedFile) {
      await processAudio(uploadedFile);
    }
  };


  const processAudio = async (file) => {
    setIsUploading(true);
    setShowLongProcessMessage(false);
    const timer = setTimeout(() => {
      setShowLongProcessMessage(true);
    }, 10000);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("language", selectedLanguageCode);
    try {
      const response = await axios.post("/api/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      let lyrics = response.data.lyrics;
      console.log(lyrics);
      await analyzePitches(lyrics, file);
    } catch (error) {
      console.error("Error uploading file:", error.response?.data);
    } finally {
      setIsUploading(false);
      setShowLongProcessMessage(false);
      clearTimeout(timer);
    }
  }

  const newAnalysis = async () => {
    setUploadedFile(null);
    setSelectedLanguageCode("");
    setSelectedScale("");
    setPitches([]);
  }

  const analyzePitches = async (lyrics, file) => {
    const formData = new FormData();
    formData.append("file", file);
    formData.append("lyrics", JSON.stringify(lyrics));
    formData.append("scale", JSON.stringify(selectedScale));
    console.log(selectedScale);
    try {
      const response = await axios.post("/api/analyze-pitches", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setPitches(response.data.pitches);
      setProcessedAudio(response.data.audio_file);
      console.log(response.data);
    } catch (error) {
      console.error("Error analyzing pitches:", error);
    }
  };

  const startRecording = async () => {
    if (uploadedFile || isRecording) return;
    setIsRecording(true);
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorderRef.current = new MediaRecorder(stream);
    audioChunksRef.current = [];
    mediaRecorderRef.current.ondataavailable = (event) => {
      audioChunksRef.current.push(event.data);
    };
    mediaRecorderRef.current.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: "audio/mp3" });
      const audioFile = new File([audioBlob], "recording.mp3", { type: "audio/mp3" });
      setUploadedFile(audioFile);
    };

    mediaRecorderRef.current.start();
  };

  const handleStopRecording = () => {
    setIsRecording(false);
    mediaRecorderRef.current.stop();
  };

  return (
    <div className="container">
      {isUploading ? (
        <div className="spinner-container">
          <ClipLoader color="#FFF" size={50} />
          {showLongProcessMessage && <p>This may take a while...</p>}
        </div>
      ) : (
        <div>
          <h1>PITCH IT</h1>
          <div className="same-line">
            <Select
              options={languageOptions}
              onChange={handleChangeLanguage}
              placeholder="LANGUAGE"
              styles={selectStyle}
              isDisabled={isRecording || isUploading}
              value={languageOptions.find(option => option.value === selectedLanguageCode)}
            />
            <Select
              options={scaleOptions}
              onChange={handleChangeScale}
              placeholder="KEY"
              styles={selectStyle}
              isDisabled={isRecording || isUploading}
              value={scaleOptions.find(option => option.value === selectedScale)}
            />
          </div>

          {selectedLanguageCode && selectedScale && !uploadedFile &&
            <div className="action-container">
              <input
                type="file"
                id="fileInput"
                className="file-input"
                onChange={handleFileUpload}
                disabled={isRecording}
                accept="audio/*"
              />
              <label htmlFor="fileInput" className="file-label">Choose a file</label>
              <span className="or-text">OR</span>
              <button
                onClick={isRecording ? handleStopRecording : startRecording}
                disabled={!!uploadedFile}
                className='file-label'
              >
                Record
              </button>
            </div>
          }

          <div className="same-line">
            {pitches && pitches.map((pitch, index) => (
              <div key={index}>
                <p>{pitch.word}</p>
                <h1>{pitch.pitch}</h1>
              </div>
            ))}
          </div>

          {uploadedFile && (
            <audio controls src={URL.createObjectURL(uploadedFile)}>
              Your browser does not support the audio element.
            </audio>
          )}

          {processedAudio && (
            <audio controls src={`data:audio/wav;base64,${processedAudio}`}>
              Your browser does not support the audio element.
            </audio>
          )}


          <div className="same-line">
            <button onClick={submitAudio} disabled={!uploadedFile || isUploading} className="submit-button">
              ANALYZE
            </button>

            {uploadedFile && 
              <button onClick={newAnalysis} className="submit-button">
                NEW ANALYSIS
              </button>
            }
          </div>

        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
