import React, { useState, useRef } from "react";
import axios from "axios";
import { ClipLoader } from "react-spinners";
import Select from "react-select";
import { LANGUAGES, SCALES } from "../constants";
import "./audioRecorder.css";

const AudioRecorder = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [showLongProcessMessage, setShowLongProcessMessage] = useState(false);
  const [selectedLanguageCode, setSelectedLanguageCode] = useState("");
  const [selectedScale, setSelectedScale] = useState("");
  const [pitches, setPitches] = useState([]);
  const [uploadedFile, setUploadedFile] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const scaleOptions = SCALES.map((scale) => ({
    value: scale,
    label: scale,
  }));

  const languageOptions = Object.entries(LANGUAGES).map(([name, code]) => ({
    value: code,
    label: name.charAt(0).toUpperCase() + name.slice(1),
  }));

  const selectStyle = {
    control: (provided) => ({
      ...provided,
      backgroundColor: "darkgray",
      color: "white",
      width: "30vw",
    }),
    singleValue: (provided) => ({
      ...provided,
      color: "white",
    }),
    option: (provided, state) => ({
      ...provided,
      backgroundColor: state.isSelected ? "gray" : "darkgray",
      color: "white",
      ":hover": {
        backgroundColor: "gray",
      },
    }),
    placeholder: (provided) => ({
      ...provided,
      color: "white",
    }),
    input: (provided) => ({
      ...provided,
      color: "white",
    }),
  };

  const handleChangeLanguage = (selectedOption) => {
    setSelectedLanguageCode(selectedOption.value);
  };

  const handleChangeScale = (selectedOption) => {
    setSelectedScale(selectedOption.value);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFile(file);
      setIsRecording(false); // Disable recording if a file is uploaded
    }
  };

  const uploadAudio = async () => {
    setIsUploading(true);
    setShowLongProcessMessage(false);

    const timer = setTimeout(() => {
      setShowLongProcessMessage(true);
    }, 10000);

    const formData = new FormData();
    if (uploadedFile) {
      formData.append("file", uploadedFile);
    } else {
      const audioBlob = new Blob(audioChunksRef.current, {
        type: "audio/mp3",
      });
      const audioFile = new File([audioBlob], "recording.mp3", {
        type: "audio/mp3",
      });
      formData.append("file", audioFile);
    }
    formData.append("language", selectedLanguageCode);
    formData.append("scale", selectedScale);

    try {
      const response = await axios.post("/api/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setPitches(response.data.pitches); // Assuming the backend sends back the pitches
    } catch (error) {
      console.error("Error uploading file:", error.response.data);
    } finally {
      setIsUploading(false);
      setShowLongProcessMessage(false);
      clearTimeout(timer);
      setUploadedFile(null); // Reset uploaded file state
    }
  };

  const startRecording = async () => {
    if (uploadedFile) return; // Do not start recording if a file has been uploaded

    setIsRecording(true);
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorderRef.current = new MediaRecorder(stream);
    audioChunksRef.current = [];
    mediaRecorderRef.current.ondataavailable = (event) => {
      audioChunksRef.current.push(event.data);
    };

    mediaRecorderRef.current.onstop = uploadAudio; // Call uploadAudio directly after recording stops

    mediaRecorderRef.current.start();
  };

  const handleStopRecording = () => {
    setIsRecording(false);
    mediaRecorderRef.current.stop();
  };

  return (
    <div className="container">
      {isUploading ? (
        <div className="spinner-container">
          <ClipLoader color="#FFF" size={50} />
          {showLongProcessMessage && <p>This may take a while...</p>}
        </div>
      ) : (
        <div>
          <h1>Pitch It</h1>
          <p>Record your audio, specify your language and your targeted key to watch the magic!</p>
          <input
            type="file"
            onChange={handleFileUpload}
            disabled={isRecording}
            accept="audio/*"
          />
          <button
            onClick={isRecording ? handleStopRecording : startRecording}
            disabled={!!uploadedFile}
            className={`record-button ${isRecording ? "recording" : ""}`}
          >
            {isRecording ? "Stop Recording" : "Start Recording"}
          </button>
          <div className="selectors">
            <Select
              options={languageOptions}
              onChange={handleChangeLanguage}
              placeholder="Language"
              styles={selectStyle}
              isDisabled={isRecording || isUploading}
            />
            <Select
              options={scaleOptions}
              onChange={handleChangeScale}
              placeholder="Key"
              styles={selectStyle}
              isDisabled={isRecording || isUploading}
            />
          </div>
          {pitches && pitches.map((pitch, index) => (
            <div key={index}>
              <p>{pitch.note}</p>
              <h1>{pitch.frequency}</h1>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
