import React from 'react';
import { useAudioRecorder } from './useAudioRecorder';
import { useFileUploader } from './useFileUploader';
import Selector from './selector';
import RecordingButton from './recordingButton';
import { LANGUAGES, SCALES } from '../constants';
import './audioRecorder.css';

const AudioRecorder = () => {

  const selectStyle = {
    control: (provided) => ({
      ...provided,
      backgroundColor: "darkgray",
      color: "white",
      width: "30vw",
    }),
    singleValue: (provided) => ({
      ...provided,
      color: "white",
    }),
    option: (provided, state) => ({
      ...provided,
      backgroundColor: state.isSelected ? "gray" : "darkgray",
      color: "white",
      ":hover": {
        backgroundColor: "gray",
      },
    }),
    placeholder: (provided) => ({
      ...provided,
      color: "white",
    }),
    input: (provided) => ({
      ...provided,
      color: "white",
    }),
  };

  const {
    isRecording,
    startRecording,
    stopRecording,
    uploadedFile,
    setUploadedFile,
    selectedLanguageCode,
    setSelectedLanguageCode,
    selectedScale,
    setSelectedScale,
    pitches,
    isUploading,
    showLongProcessMessage,
  } = useFileUploader(useAudioRecorder());

  return (
    <div className="container">
      {isUploading ? (
        <div className="spinner-container">
          <ClipLoader color="#FFF" size={50} />
          {showLongProcessMessage && <p>This may take a while...</p>}
        </div>
      ) : (
        <div>
          <h1>Pitch It</h1>
          <input
            type="file"
            onChange={(event) => setUploadedFile(event.target.files[0])}
            disabled={isRecording}
            accept="audio/*"
          />
          <RecordingButton
            isRecording={isRecording}
            startRecording={startRecording}
            stopRecording={stopRecording}
            uploadedFile={uploadedFile}
          />
          <div className="selectors">
            <LanguageSelector
              options={Object.entries(LANGUAGES).map(([name, code]) => ({
                value: code,
                label: name.charAt(0).toUpperCase() + name.slice(1),
              }))}
              selectedValue={selectedLanguageCode}
              onChange={setSelectedLanguageCode}
              isDisabled={isRecording || isUploading}
            />
            <Selector
              options={SCALES.map((scale) => ({
                value: scale,
                label: scale,
              }))}
              selectedValue={selectedScale}
              name="Scale"
              onChange={setSelectedScale}
              isDisabled={isRecording || isUploading}
              selectStyle={selectStyle}
            />
            <Selector
              options={LANGUAGES.map((scale) => ({
                value: scale,
                label: scale,
              }))}
              selectedValue={selectedLanguageCode}
              name="Language"
              onChange={setSelectedLanguageCode}
              isDisabled={isRecording || isUploading}
              selectStyle={selectStyle}
            />
          </div>
          {pitches.map((pitch, index) => (
            <div key={index}>
              <p>{pitch.note}</p>
              <h1>{pitch.frequency}</h1>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
